  user: string;
  car: string;
  branch: "JLLE" | "CWB" | "MGA" | "POA";
  out: string; // ISO date
  status: ReservationStatus;
};

const MOCK_RESERVATIONS: Reservation[] = [
  { id: "R1", user: "Alex Morgan",  car: "BMW X5",        branch: "JLLE", status: "Approved",  out: "2025-10-02" },
